import React from 'react';
import axios from 'axios';

const access_token = 'BQDlbG_OFHE879A8aAry1yURie8EfFHx0nK6dUYxu_dkH5yVB7mOSW1'

class GetRequest extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            track_name: null
        };
    }

    componentDidMount() {
        // Simple GET request using fetch
        axios.get('https://api.github.com/user', {
        headers: {
            'Authorization': `token ${access_token}`
            }
        })
        .then((res) => this.setState({track_name : res.data}))
    }

    render() {
        const { track_name } = this.state;
        return (
            <div className="card text-center m-3">
                <h5 className="card-header">Simple GET Request</h5>
                <div >
                    {track_name}
                </div>
            </div>
        );
    }
}

export { GetRequest };
